import React from 'react';
import GridView from './GridView';
import CarouselView from './CarouselView';
import Button from 'react-bootstrap/Button';
import './App.css';

export default class HomePhotos extends React.Component {
    constructor()   {
        super();
        this.state =  {
            view: 'grid',
            index: 0
        }
        this.handleClick = this.handleClick.bind(this);
        this.handlePhotoClick = this.handlePhotoClick.bind(this);

    }

    handlePhotoClick = (e)   =>  {
        console.log(e.target.alt)
        let idx = Number(e.target.alt)
        this.setState({view: 'carousel',index: idx})
    }

    handleClick = () => {
        if (this.state.view === 'grid') {
            this.setState({view: 'carousel'})
        }   else    {
            this.setState({view: 'grid'})
        }
    }
  render () {
    return (
            <div>
                <div id="title">
                    <h3>Binghamton, New York <span className="russ">| Бингемтон, Нью-Йорк</span></h3>
                    <h4>Winter, 2020 - 2021 <span className="russ">| Зима</span></h4>
                </div>
                <div>
                <Button id="toggle-view-btn" onClick={this.handleClick}>Click to toggle view</Button>
                </div>
                {this.state.view === 'grid' ? <GridView handleClick={this.handlePhotoClick}/> : <CarouselView/>}
            </div>
    )
  }
}
