import * as React from "react";
interface ImageData {
    id: string;
    src: string;
}
declare type StateImageData = ImageData & {
    width: number;
    height: number;
};
interface Props {
    data: Array<string> | Array<ImageData>;
    onImageClick: (image: string) => void;
    gridSize?: string;
    containerWidth?: number;
}
interface State {
    ladyLuck: number;
    containerWidth: number;
    containerHeight: number;
    imagesToShow: Array<StateImageData>;
}
declare class ReactPhotoGrid extends React.Component<Props, State> {
    private containerRef;
    constructor(props: Props);
    componentWillUnmount(): void;
    componentDidMount(): void;
    onResize: () => void;
    handleImageClick: (imageSrc: string) => void;
    recalculateGrid: (id: string, width: number, height: number) => void;
    getComponentStyles: (images: StateImageData[]) => any[];
    render(): JSX.Element;
}
export default ReactPhotoGrid;
